float power(float num,int p){
	int j;
	float n=1;
	if(p<0)
	{ 
		p*=-1; 
		num=1/num;
	}
	
	for(j=30;j>0;j--)
	{
		if(p&(1<<j))
			n*=num;
		n*=n;
		if(n>(30000))
		{
			n=30000;
			break;
		}
	}
	if(p&1)
		n*=num;
	
	return n;
}


