void WriteTouch_XPT2046(unsigned char num){
	unsigned char count=0;
	GPIO_PORTA_DATA_R|=0x08;
	
	for(count=0;count<8;count++)
	{
		if(num&0x80)
			GPIO_PORTA_DATA_R|=0x10;
		else
			GPIO_PORTA_DATA_R&=~0x10;
		
		GPIO_PORTA_DATA_R&=~0x08;
		GPIO_PORTA_DATA_R|=0x08;
		num<<=1;
	}
}


unsigned int ReadTouch_XPT2046(){
	unsigned char count=0;
	unsigned int num=0;
	for(count=0;count<12;count++)	
	{
		GPIO_PORTA_DATA_R|=0x08;
		GPIO_PORTA_DATA_R&=~0x08;
		if(GPIO_PORTA_DATA_R&0x20)
			num++;
		num<<=1;
	}
	return num;
}


void XPT2046(void){
	GPIO_PORTA_DATA_R&=~0x04;
	
	WriteTouch_XPT2046(0x90);    
	GPIO_PORTA_DATA_R|=0x08;
	GPIO_PORTA_DATA_R&=~0x08;delayus(1);
	tx=ReadTouch_XPT2046();
	
	WriteTouch_XPT2046(0xD0);
	GPIO_PORTA_DATA_R|=0x08;
	GPIO_PORTA_DATA_R&=~0x08; delayus(1);
	ty=ReadTouch_XPT2046();
	
	GPIO_PORTA_DATA_R|=0x04;
}


void Ports_Init(){
	SYSCTL_RCGC2_R|=0x3F;	
	delayus(2);
	
	GPIO_PORTA_AMSEL_R = 0x00;        // 3) disable analog on PF
  GPIO_PORTA_PCTL_R = 0x00000000;   // 4) PCTL GPIO on PF4-0
  GPIO_PORTA_DIR_R = 0x1C;          // 5) PA5-6 in, PA2-4 out
  GPIO_PORTA_AFSEL_R = 0x00;        // 6) disable alt funct on PF7-0
  GPIO_PORTA_DEN_R = 0x7C;          // 7) enable digital I/O on PF4-0
	
	GPIO_PORTB_AMSEL_R = 0x00;        // 3) disable analog on PB
  GPIO_PORTB_PCTL_R = 0x00000000;   // 4) PCTL GPIO on PB5-0
  GPIO_PORTB_DIR_R = 0x3F;          // 5) PB5-0 out
  GPIO_PORTB_AFSEL_R = 0x00;        // 6) disable alt funct on PB5-0
  GPIO_PORTB_DEN_R = 0x3F;          // 7) enable digital I/O on PB5-0
	
	GPIO_PORTC_AMSEL_R &=~ 0xF0;        // 3) disable analog on PC
  GPIO_PORTC_PCTL_R &=~ 0xFFFF0000;   // 4) PCTL GPIO on PC4-0
  GPIO_PORTC_DIR_R |= 0xF0;          // 5) PC4-8 out
  GPIO_PORTC_AFSEL_R &=~ 0xF0;        // 6) disable alt funct on PC4-8
  GPIO_PORTC_DEN_R |= 0xF0;          // 7) enable digital I/O on PC4-8
	
	GPIO_PORTD_AMSEL_R = 0x00;        // 3) disable analog on PD
  GPIO_PORTD_PCTL_R = 0x00000000;   // 4) PCTL GPIO on PD0-3,6
  GPIO_PORTD_DIR_R = 0x4F;          // 5) PD0-3,6 out
  GPIO_PORTD_AFSEL_R = 0x00;        // 6) disable alt funct on PD7-0
  GPIO_PORTD_DEN_R = 0x4F;          // 7) enable digital I/O on PD0-3,6
	
	GPIO_PORTE_AMSEL_R = 0x00;        // 3) disable analog on PE
  GPIO_PORTE_PCTL_R = 0x00000000;   // 4) PCTL GPIO on PE5-0
  GPIO_PORTE_DIR_R = 0x3F;          // 5) PE5-0 out
  GPIO_PORTE_AFSEL_R = 0x00;        // 6) disable alt funct on PE5-0
  GPIO_PORTE_DEN_R = 0x3F;          // 7) enable digital I/O on PE5-0
}


void LCD_Write_C_D(unsigned short data,char dc){ //COM0   DATA1	
	if(dc==1)				//rs=0/1
		GPIO_PORTD_DATA_R|=0x08;
  else if(dc==0)
    GPIO_PORTD_DATA_R&=~0x08;

	GPIO_PORTB_DATA_R=0x3F & data;
	GPIO_PORTE_DATA_R=0x3F & data>>6;
	GPIO_PORTC_DATA_R=0xF0 & data>>8;

	GPIO_PORTD_DATA_R&=~0x04;       //wr
	GPIO_PORTD_DATA_R|=0x04;
}


void Address_set_ILI9341(signed short x1,signed short y1,signed short x2,signed short y2){
	GPIO_PORTD_DATA_R&=~0x01;      //cs
	
	if(x2>319)
		x2=319;
	else if(x2<0)
		x2=0;
	
	if(y2>239)
		y2=239;
	else if(y2<0)
		y2=0;
	
	if(x1<0)
		x1=0;
	else if(x1>319)
		x1=319;
	
	if(y1<0)
		y1=0;
	else if(y1>239)
		y1=239;
	
	if(x1>x2)
	{	
		x1+=x2;
		x2=x1-x2;
		x1=x1-x2;
	}
	if(y1>y2)
	{	
		y1+=y2;
		y2=y1-y2;
		y1=y1-y2;
	}
	
	LCD_Write_C_D(0x002A,0);
	LCD_Write_C_D(((x1>>8)&0xff),1);
  LCD_Write_C_D((x1&0xff),1);
	LCD_Write_C_D(((x2>>8)&0xff),1);
  LCD_Write_C_D((x2&0xff),1);
	
	LCD_Write_C_D(0x002B,0);
	LCD_Write_C_D(((y1>>8)&0xff),1);
  LCD_Write_C_D((y1&0xff),1);
	LCD_Write_C_D(((y2>>8)&0xff),1);
  LCD_Write_C_D((y2&0xff),1);
	
  LCD_Write_C_D(0x002C,0);
	GPIO_PORTD_DATA_R|=0x01;      //cs
}


