void gettouchxy(){
	int i,j;
	i=j=ti=0;
	while((ti<5)&&!(GPIO_PORTA_DATA_R&0x40))
	{
		delayms(5);
		XPT2046();
		ti++;
		i+=tx;
		j+=ty;
	}
	tx=i/ti;
	ty=j/ti;
	
	tx=20+319-tx/23;
	ty=-43+ty/26;
}


