void disp_char(char *ch,short x,short y,short color){
	int i,j;
	Address_set_ILI9341(x,y,x+ch[0]-1,y+ch[1]-1);
	GPIO_PORTD_DATA_R&=~0x01;        //cs=0 
	for(i=3;i<=(ch[1]*ch[0])/8;i++)
		for(j=7;j>=0;j--)
		{
			if(ch[i]&(1<<j))
				LCD_Write_C_D(color,1);
			else
				LCD_Write_C_D(0x0000,1);
		}
	GPIO_PORTD_DATA_R|=0x01;         //cs=1 
}


