void retrieve_func(){
	int i,temp;
	unsigned char muldiv_f,neg_f,func_f,temp_f;      
	// func_f                                          scale/shift   infunc
	//   7       6       5       4       3       2         1            0
	// muldiv_f                                           div          mul
	//   7       6       5       4       3       2         1            0
	i=0;temp=0;
	muldiv_f=neg_f=func_f=temp_f=0;
	for(i=0;i<func_len;i++)
	{
		if(functn[i][2]>47 && functn[i][2]<58)
		{
			if(temp_f==0)
			{temp_f=1;temp=0;}
			temp*=10;
			temp+= (functn[i][2]-48);
			continue;
		}
			
		if(temp_f)
		{
			temp_f=0;
			if(neg_f)
			{
				temp*=-1;
				neg_f=0;				
			}
			if(func_f==0)
			{
				if(muldiv_f!=2)
					elements[num_elements].mul*=temp;
				else
					elements[num_elements].div*=temp;
			}
			else if(func_f==1)
			{
				if(muldiv_f!=2)
					elements[num_elements].scale_mul*=temp;
				else
					elements[num_elements].scale_div*=temp;
			}
			else if(func_f==3)
			{
				if(muldiv_f!=2)
					elements[num_elements].shift_mul+=temp;
				else
					elements[num_elements].shift_div*=temp;
			}
			muldiv_f=0;
		}
				
		if((functn[i]==multiply)||(functn[i]==divide))
		{
			if(functn[i]==divide)
				muldiv_f=2;
			else
				muldiv_f=1;
			continue;
		}
		
		if(functn[i][2]=='f')
		{
			if(elements[num_elements].type!='-')
			{
				if(muldiv_f==2)
					ele_func[num_elements]='/';
				else 
					ele_func[num_elements]='*';
				muldiv_f=0;
				num_elements++;
			}
			
			if(functn[i]==cosx)
				elements[num_elements].type='c';
			if(functn[i]==sinx)
				elements[num_elements].type='s';
			if(functn[i]==tanx)
				elements[num_elements].type='t';
			if(functn[i]==ex)
				elements[num_elements].type='e';
			if(functn[i]==x)
				elements[num_elements].type='x';
			func_f=1;
			continue;
		}
		
		if(functn[i]==clbrac)
		{
			func_f=0;
			continue;
		}

		if((functn[i]==plus) || (functn[i]==minus))
		{	
			if(func_len==0)
			{
				if(functn[i]==minus)
					neg_f=1;
				continue;
			}
			if(muldiv_f!=0)
			{
				neg_f=1;
				continue;
			}
				
			if(func_f==0)
			{
				ele_func[num_elements]='+';
				num_elements++;
			}
			if(func_f==1)
			{
				if(elements[num_elements].type=='x')
				{
					func_f=0;
					ele_func[num_elements]='+';
					num_elements++;
				}
				else
					func_f=3;
			}
			if(functn[i]==minus)
					neg_f=1;
			continue;	
		}
	}
	
	for(i=0;i<=num_elements;i++)
	{
		if(elements[i].div==0)
			elements[i].div=1;
		if(elements[i].scale_div==0)
			elements[i].scale_div=1;
		if(elements[i].shift_div==0)
			elements[i].shift_div=1;
		elements[i].multiplier=(float)elements[i].mul/elements[i].div;
		elements[i].scale=(float)elements[i].scale_mul/elements[i].scale_div;
		elements[i].shift=(float)elements[i].shift_mul/elements[i].shift_div;
		if(elements[i].multiplier>100)
			elements[i].multiplier=100;
		if(elements[i].scale>15)
			elements[i].scale=15;
		if(elements[i].shift>200)
			elements[i].shift=200;
	}
	
}


float sin_x(int n){
	int i=1;
	if(n<0)
	{
		i=-1;
		n*=-1;
	}
	n%=360;
	if(n>180)
	{
		n%=180;
		i*=-1;
	}
	if(n<=90)
		return i*sincosval[n];
	else
		return i*sincosval[180-n];
}


float cos_x(int n){
	int i=1;
	if(n<0)
	{
		n*=-1;
	}
	n%=360;
	if((n>90)&&(n<270))
		i*=-1;
	if(n>180)
		n%=180;
	
	if(n<=90)
		return i*sincosval[90-n];
	else
		return i*sincosval[n-91];
}


float tan_x(int n){
	float temp;
	temp=cos_x(n);
	if(temp==0)
		return 10000;
	temp=sin_x(n)/temp;
	return temp;
}


float e_x(int n){
	float temp;
	
	temp=power(e1000,n);
	return temp;
}


