float calc(float x){
	int i;
	float temp,temp2;
	
	for(i=0;i<10;i++)
		vals[i]=0;
	
	for(i=0;i<=num_elements;i++)
	{
		if(elements[i].type=='x')
		{
			vals[i]=power(x,(int)elements[i].scale);
			vals[i]*=elements[i].multiplier;
			continue;
		}
		if(elements[i].type=='-')
		{
			vals[i]=elements[i].multiplier;
			continue;
		}
		
		x*=elements[i].scale;
		x+=elements[i].shift;
		if(elements[i].type=='s')
			vals[i]=sin_x((int)(rad_deg_mul*x));
		else if(elements[i].type=='c')
			vals[i]=cos_x((int)(rad_deg_mul*x));
		else if(elements[i].type=='t')
			vals[i]=tan_x((int)(rad_deg_mul*x));
		else if(elements[i].type=='e')
			vals[i]=e_x((int)(x*1000));
				
		vals[i]*=elements[i].multiplier;
	}
		
	temp2=vals[0];
	temp=0;
	for(i=1;i<=num_elements;i++)
	{
		if(ele_func[i-1]=='*')
			temp2*=vals[i];
		else if(ele_func[i-1]=='/')
			temp2/=vals[i];
		else
		{
			temp+=temp2;
			temp2=vals[i];
		}
	}
	temp+=temp2;
	return temp;
}


